class ControllerExtensionShippingFaixaCep extends Controller
{
    private $error = array();

    public function index()
    {
        $this->load->language('extension/shipping/faixa_cep');

        $this->document->setTitle($this->language->get('heading_title'));

        $this->load->model('setting/setting');

        if (($this->request->server['REQUEST_METHOD'] == 'POST') && $this->validate()) {
            $segredo = sha1(md5(sha1(md5(sha1(md5(sha1('' . preg_replace('/www\./i', '', $_SERVER['HTTP_HOST'] . ''))))))));
            $chave = $this->request->post['shipping_faixa_cep_chave'];
            if ($segredo == $chave) {
                $this->model_setting_setting->editSetting('shipping_faixa_cep', $this->request->post);

                $this->session->data['success'] = $this->language->get('text_success');

                if (isset($this->request->post['save_stay']) && ($this->request->post['save_stay'] = 1)) {
                    $this->response->redirect($this->url->link('extension/shipping/faixa_cep', 'user_token=' . $this->session->data['user_token'], true));
                } else {
                    $this->response->redirect($this->url->link('marketplace/extension', 'user_token=' . $this->session->data['user_token'] . '&type=shipping', true));
                }
            } else {
                $this->error['warning'] = 'A chave da extensão não é válida, entre em contato com o suporte para mais esclarecimentos.';
                $this->error['chave'] = 'A chave da extensão não é válida.';
            }
        }

        $data['entry_chave'] = 'Chave da extensão';
        $data['help_chave'] = 'A chave da extensão é fornecida pelo OpenCart Brasil.';

        if (isset($this->session->data['success'])) {
            $data['success'] = $this->session->data['success'];

            unset($this->session->data['success']);
        } else {
            $data['success'] = '';
        }

        $data['user_token'] = $this->session->data['user_token'];

        $erros = array(
            'warning',
            'chave',
            'titulo'
        );

        foreach ($erros as $erro) {
            if (isset($this->error[$erro])) {
                $data['error_' . $erro] = $this->error[$erro];
            } else {
                $data['error_' . $erro] = '';
            }
        }

        $data['breadcrumbs'] = array();

        $data['breadcrumbs'][] = array(
            'text' => $this->language->get('text_home'),
            'href' => $this->url->link('common/dashboard', 'user_token=' . $this->session->data['user_token'], true)
        );

        $data['breadcrumbs'][] = array(
            'text' => $this->language->get('text_extension'),
            'href' => $this->url->link('marketplace/extension', 'user_token=' . $this->session->data['user_token'] . '&type=shipping', true)
        );

        $data['breadcrumbs'][] = array(
            'text' => $this->language->get('heading_title'),
            'href' => $this->url->link('extension/shipping/faixa_cep', 'user_token=' . $this->session->data['user_token'], true)
        );

        $data['action'] = $this->url->link('extension/shipping/faixa_cep', 'user_token=' . $this->session->data['user_token'], true);

        $data['cancel'] = $this->url->link('marketplace/extension', 'user_token=' . $this->session->data['user_token'] . '&type=shipping', true);

        $data['versao'] = '2.0.0';

        $campos = array(
            'chave' => '',
            'tipo_custo' => '',
            'custo' => '',
            'tax_class_id' => '',
            'geo_zone_id' => '',
            'status' => '',
            'sort_order' => '',
            'titulo' => '',
            'imagem' => ''
        );

        foreach ($campos as $campo => $valor) {
            if (!empty($valor)) {
                if (isset($this->request->post['shipping_faixa_cep_' . $campo])) {
                    $data['shipping_faixa_cep_' . $campo] = $this->request->post['shipping_faixa_cep_' . $campo];
                } else if ($this->config->get('shipping_faixa_cep_' . $campo)) {
                    $data['shipping_faixa_cep_' . $campo] = $this->config->get('shipping_faixa_cep_' . $campo);
                } else {
                    $data['shipping_faixa_cep_' . $campo] = $valor;
                }
            } else {
                if (isset($this->request->post['shipping_faixa_cep_' . $campo])) {
                    $data['shipping_faixa_cep_' . $campo] = $this->request->post['shipping_faixa_cep_' . $campo];
                } else {
                    $data['shipping_faixa_cep_' . $campo] = $this->config->get('shipping_faixa_cep_' . $campo);
                }
            }
        }

        $this->load->model('catalog/product');

        $data['produtos'] = array();

        if (isset($this->request->post['shipping_faixa_cep_produtos'])) {
            $produtos = $this->request->post['shipping_faixa_cep_produtos'];
        } elseif ($this->config->get('shipping_faixa_cep_produtos')) {
            $produtos = $this->config->get('shipping_faixa_cep_produtos');
        } else {
            $produtos = array();
        }

        foreach ($produtos as $product_id) {
            $product_info = $this->model_catalog_product->getProduct($product_id);

            if ($product_info) {
                $data['produtos'][] = array(
                    'product_id' => $product_info['product_id'],
                    'name' => $product_info['name']
                );
            }
        }

        $this->load->model('localisation/tax_class');
        $data['tax_classes'] = $this->model_localisation_tax_class->getTaxClasses();

        $this->load->model('localisation/geo_zone');
        $data['geo_zones'] = $this->model_localisation_geo_zone->getGeoZones();

        $this->load->model('tool/image');

        if (isset($this->request->post['shipping_faixa_cep_imagem']) && is_file(DIR_IMAGE . $this->request->post['shipping_faixa_cep_imagem'])) {
            $data['thumb'] = $this->model_tool_image->resize($this->request->post['shipping_faixa_cep_imagem'], 100, 100);
        } elseif (is_file(DIR_IMAGE . $this->config->get('shipping_faixa_cep_imagem'))) {
            $data['thumb'] = $this->model_tool_image->resize($this->config->get('shipping_faixa_cep_imagem'), 100, 100);
        } else {
            $data['thumb'] = $this->model_tool_image->resize('no_image.png', 100, 100);
        }

        $data['no_image'] = $this->model_tool_image->resize('no_image.png', 100, 100);

        $this->update();

        $data['header'] = $this->load->controller('common/header');
        $data['column_left'] = $this->load->controller('common/column_left');
        $data['footer'] = $this->load->controller('common/footer');

        $this->response->setOutput($this->load->view('extension/shipping/faixa_cep', $data));
    }

    public function faixas()
    {
        $this->load->language('extension/shipping/faixa_cep');

        $data['user_token'] = $this->session->data['user_token'];

        if (isset($this->request->get['page'])) {
            $page = $this->request->get['page'];
        } else {
            $page = 1;
        }

        if (isset($this->request->get['filter_value'])) {
            $filter_value = $this->request->get['filter_value'];
        } else {
            $filter_value = '';
        }

        $filter = ($filter_value) ? '&filter_value=' . $filter_value : '';

        $filter_data = array(
            'filter_value' => $filter_value,
            'start' => ($page - 1) * $this->config->get('config_limit_admin'),
            'limit' => $this->config->get('config_limit_admin')
        );

        $this->load->model('extension/shipping/faixa_cep');
        $results = $this->model_extension_shipping_faixa_cep->getFaixasCep($filter_data);

        $data['faixas'] = array();

        foreach ($results as $result) {
            $data['faixas'][] = array(
                'faixa_cep_id' => $result['faixa_cep_id'],
                'descricao' => $result['descricao'],
                'cep_inicial' => $result['cep_inicial'],
                'cep_final' => $result['cep_final'],
                'peso_minimo' => $result['peso_minimo'],
                'peso_maximo' => $result['peso_maximo'],
                'total_minimo' => $result['total_minimo'],
                'preco' => $result['preco']
            );
        }

        $faixas_total = $this->model_extension_shipping_faixa_cep->getTotalFaixasCep($filter_data);

        $pagination = new Pagination();
        $pagination->total = $faixas_total;
        $pagination->page = $page;
        $pagination->limit = $this->config->get('config_limit_admin');
        $pagination->url = $this->url->link('extension/shipping/faixa_cep/faixas', 'user_token=' . $this->session->data['user_token'] . '&page={page}' . $filter, true);

        $data['pagination'] = $pagination->render();

        $data['results'] = sprintf($this->language->get('text_pagination'), ($faixas_total) ? (($page - 1) * $this->config->get('config_limit_admin')) + 1 : 0, ((($page - 1) * $this->config->get('config_limit_admin')) > ($faixas_total - $this->config->get('config_limit_admin'))) ? $faixas_total : ((($page - 1) * $this->config->get('config_limit_admin')) + $this->config->get('config_limit_admin')), $faixas_total, ceil($faixas_total / $this->config->get('config_limit_admin')));

        $this->response->setOutput($this->load->view('extension/shipping/faixa_cep_list', $data));
    }

    public function save()
    {
        $json = array();

        $this->load->language('extension/shipping/faixa_cep');

        if ($this->user->hasPermission('modify', 'extension/shipping/faixa_cep')) {
            if (isset($this->request->post)) {
                $campos = array(
                    'descricao' => $this->request->post['descricao'],
                    'cep_inicial' => $this->request->post['inicial'],
                    'cep_final' => $this->request->post['final'],
                    'peso_minimo' => $this->request->post['minimo'],
                    'peso_maximo' => $this->request->post['maximo'],
                    'total_minimo' => $this->request->post['total'],
                    'preco' => $this->request->post['preco']
                );

                $this->load->model('extension/shipping/faixa_cep');
                if (isset($this->request->post['id'])) {
                    $campos['faixa_cep_id'] = $this->request->post['id'];
                    $this->model_extension_shipping_faixa_cep->editFaixaCep($campos);
                    $json['success'] = $this->language->get('text_update');
                } else {
                    $this->model_extension_shipping_faixa_cep->addFaixaCep($campos);
                    $json['success'] = $this->language->get('text_add');
                }
            }
        } else {
            $json['error'] = $this->language->get('error_permission');
        }

        $this->response->addHeader('Content-Type: application/json');
        $this->response->setOutput(json_encode($json));
    }

    public function edit()
    {
        $json = array();

        $this->load->language('extension/shipping/faixa_cep');

        if ($this->user->hasPermission('modify', 'extension/shipping/faixa_cep')) {
            if (isset($this->request->get['id'])) {

                $this->load->model('extension/shipping/faixa_cep');
                $faixa_cep_info = $this->model_extension_shipping_faixa_cep->getFaixaCep($this->request->get['id']);

                if ($faixa_cep_info) {
                    $json['success'] = true;
                    $json['id'] = $faixa_cep_info['faixa_cep_id'];
                    $json['descricao'] = $faixa_cep_info['descricao'];
                    $json['inicial'] = $faixa_cep_info['cep_inicial'];
                    $json['final'] = $faixa_cep_info['cep_final'];
                    $json['minimo'] = $faixa_cep_info['peso_minimo'];
                    $json['maximo'] = $faixa_cep_info['peso_maximo'];
                    $json['total'] = $faixa_cep_info['total_minimo'];
                    $json['preco'] = $faixa_cep_info['preco'];
                }
            }
        } else {
            $json['error'] = $this->language->get('error_permission');
        }

        $this->response->addHeader('Content-Type: application/json');
        $this->response->setOutput(json_encode($json));
    }

    public function delete()
    {
        $json = array();

        $this->load->language('extension/shipping/faixa_cep');

        if ($this->user->hasPermission('modify', 'extension/shipping/faixa_cep')) {
            if (isset($this->request->get['id'])) {

                $this->load->model('extension/shipping/faixa_cep');
                $this->model_extension_shipping_faixa_cep->deleteFaixaCep($this->request->get['id']);

                $json['success'] = $this->language->get('text_delete');
            }
        } else {
            $json['error'] = $this->language->get('error_permission');
        }

        $this->response->addHeader('Content-Type: application/json');
        $this->response->setOutput(json_encode($json));
    }

    protected function validate()
    {
        if (!$this->user->hasPermission('modify', 'extension/shipping/faixa_cep')) {
            $this->error['warning'] = $this->language->get('error_permission');
        }

        if (!(trim($this->request->post['shipping_faixa_cep_chave']))) {
            $this->error['chave'] = 'O campo Chave da extensão é de preenchimento obrigatório!';
        }

        $erros = array(
            'titulo'
        );

        foreach ($erros as $erro) {
            if (!(trim($this->request->post['shipping_faixa_cep_' . $erro]))) {
                $this->error[$erro] = $this->language->get('error_' . $erro);
            }
        }

        if ($this->error && !isset($this->error['warning'])) {
            $this->error['warning'] = $this->language->get('error_warning');
        }

        return !$this->error;
    }

    public function install()
    {
        $this->load->model('extension/shipping/faixa_cep');
        $this->model_extension_shipping_faixa_cep->install();
    }

    public function uninstall()
    {
        $this->load->model('extension/shipping/faixa_cep');
        $this->model_extension_shipping_faixa_cep->uninstall();
    }

    public function update()
    {
        $this->load->model('extension/shipping/faixa_cep');
        $this->model_extension_shipping_faixa_cep->updateTable();
    }
}